class Number2 {
    public static void main(String[] args){
        int[][] a ={
            {10,20,30},
            {40,50},
            {60,70,80,90}
        };
        System.out.println("一次元配列の様素数" + a.length);
        System.out.println("a[0]の要素数" + a[0].length);
        System.out.println("a[1]の要素数" + a[1].length);
        System.out.println("a[2]の要素数" + a[2].length);
    }
}
